package loop_demo;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		for(int i=0;i<=10;i++)
		{
			System.out.println(i);
		}
		
		System.out.println("---------------------");
		
		
		for(int i=0;i<=10;i+=2)
		{
			System.out.println(i);
		}
		
		System.out.println("---------------------");
		
		
		
		for(int i=1;i<=10;i+=2)
		{
			System.out.println(i);
		}
		
		System.out.println("---------------------");
		
		
		int i = 0;
		
		while(i<=10)
		{
		System.out.println(i);
		i++;
		}
	
		System.out.println("---------------------");
		
		int y = 0;
		while(y<=10)
		{
		System.out.println(y);
		y+=2;
		}
		
		System.out.println("---------------------");
		
		int z = 1;
		while(z<=10)
		{
		System.out.println(z);
		z+=2;
		}
		
		System.out.println("---------------------");
			
		
		int a=0;
		do
		{
			
		System.out.println(a);
		a++;
			
		}
		
		while(a<=10);
		System.out.println("---------------------");
		
		
		int b=0;
		
		do
		{
			
			System.out.println(b);
			b+=2;
			
		}
		while(b<=10);
		System.out.println("---------------------");
		
		
		int c=1;
		
		do
		{
			
			System.out.println(c);
			c+=2;
			
		}
		
		while(c<=10);
		System.out.println("---------------------");
		
		
	}

}
