package switch_demo;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		char point = 1;
		
		switch(point)
		
		{
		case 5:
			System.out.println("Pekiyi!");
			break;
			
		case 4:
			System.out.println("Iyi!");
			break;
			
		case 3:
			System.out.println("Idare eder...");
			break;
			
		case 2:
			System.out.println("Sinava hic girmesesydiniz...");
			break;
			
		case 1:
			System.out.println("Gozum sizi gormesin!!!");
			break;
		
			default:
				System.out.println("Lutfen duzgun bir deger giriniz!");
		}
		
	}

}
